mapper.select:
SELECT 

FROM sys_column_hide a

WHERE a.id = #{id}

SELECT
FROM sys_column_hide a
AND a.id = #{id}
AND a.page_name LIKE '%'||#{pageName}||'%' '%'+#{pageName}+'%' concat('%',#{pageName},'%')
AND a.class_name = #{className}
AND a.column_hide_arr LIKE '%'||#{columnHideArr}||'%' '%'+#{columnHideArr}+'%' concat('%',#{columnHideArr},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM sys_column_hide a






		ORDER BY ${page.orderBy}

mapper.insert:
INSERT INTO sys_column_hide(
	page_name,
	class_name,
	column_hide_arr
) VALUES (
	#{pageName},
	#{className},
	#{columnHideArr}
)

mapper.update:
UPDATE sys_column_hide SET 	
	page_name = #{pageName},
	class_name = #{className},
	column_hide_arr = #{columnHideArr}
WHERE id = #{id}
