mapper.select:
SELECT 

FROM gen_menu a

WHERE a.id = #{id}

SELECT
FROM gen_menu a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.menu_name LIKE '%'||#{menuName}||'%' '%'+#{menuName}+'%' concat('%',#{menuName},'%')
AND a.create_date = #{createDate}
AND a.href LIKE '%'||#{href}||'%' '%'+#{href}+'%' concat('%',#{href},'%')
AND a.view_perm_url LIKE '%'||#{viewPermUrl}||'%' '%'+#{viewPermUrl}+'%' concat('%',#{viewPermUrl},'%')
AND a.edit_perm_url LIKE '%'||#{editPermUrl}||'%' '%'+#{editPermUrl}+'%' concat('%',#{editPermUrl},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM gen_menu a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gen_menu SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
