AND a.code_name LIKE '%'||#{codeName}||'%' '%'+#{codeName}+'%' concat('%',#{codeName},'%')
AND a.code_content LIKE '%'||#{codeContent}||'%' '%'+#{codeContent}+'%' concat('%',#{codeContent},'%')
AND a.create_time = #{createTime}
