mapper.select:
SELECT 

FROM ps_match_process a

WHERE a.id = #{id}

SELECT 

FROM ps_match_process a




		AND a.id = #{id}


		AND a.process_name LIKE 
			'%'||#{processName}||'%'
			'%'+#{processName}+'%'
			concat('%',#{processName},'%')


		AND a.match_id = #{matchId}


		AND a.start_time >= #{startTime}


		AND a.end_time <= #{endTime}


		AND a.project_id = #{projectId}


		AND a.self_signup = #{selfSignup}


		AND a.has_leaderboard = #{hasLeaderboard}


		AND a.eval_docker_image LIKE 
			'%'||#{evalDockerImage}||'%'
			'%'+#{evalDockerImage}+'%'
			concat('%',#{evalDockerImage},'%')


		AND a.token_start_time >= #{tokenStartTime}




		ORDER BY ${page.orderBy}

SELECT 

FROM ps_match_process a






		ORDER BY ${page.orderBy}
