mapper.select:
SELECT 

FROM ps_match_submit a

WHERE a.id = #{id}

SELECT 

FROM ps_match_submit a




		AND a.id = #{id}


		AND a.submit_name = #{submitName}


		AND a.version = #{version}


		AND a.create_time >= #{createTime}


		AND a.status_code = #{statusCode}


		AND a.last_update_time >= #{lastUpdateTime}


		AND a.user_id = #{userid}


		AND a.score >= #{score}


		AND a.bos_key LIKE 
			'%'||#{bosKey}||'%'
			'%'+#{bosKey}+'%'
			concat('%',#{bosKey},'%')


		AND a.bos_file_url LIKE 
			'%'||#{bosFileUrl}||'%'
			'%'+#{bosFileUrl}+'%'
			concat('%',#{bosFileUrl},'%')


		AND a.start_time >= #{startTime}


		AND a.end_time <= #{endTime}


		AND a.process_id = #{processId}


		AND a.anonymous = #{anonymous}


		AND a.reference LIKE 
			'%'||#{reference}||'%'
			'%'+#{reference}+'%'
			concat('%',#{reference},'%')


		AND a.introduction LIKE 
			'%'||#{introduction}||'%'
			'%'+#{introduction}+'%'
			concat('%',#{introduction},'%')


		AND a.project_id = #{projectId}


		AND a.match_id = #{matchId}


		AND a.error_msg = #{errorMsg}




		ORDER BY ${page.orderBy}

SELECT 

FROM ps_match_submit a






		ORDER BY ${page.orderBy}
