mapper.select:
SELECT 

FROM ps_user a

WHERE a.id = #{id}

SELECT 

FROM ps_user a




		AND a.id = #{id}


		AND a.user_name LIKE 
			'%'||#{userName}||'%'
			'%'+#{userName}+'%'
			concat('%',#{userName},'%')


		AND a.user_source_id = #{userSourceId}


		AND a.user_type = #{userType}


		AND a.create_time >= #{createTime}


		AND a.last_login_time >= #{lastLoginTime}


		AND a.status_code = #{statusCode}


		AND a.is_employee = #{isEmployee}


		AND a.display_name LIKE 
			'%'||#{displayName}||'%'
			'%'+#{displayName}+'%'
			concat('%',#{displayName},'%')


		AND a.true_name LIKE 
			'%'||#{trueName}||'%'
			'%'+#{trueName}+'%'
			concat('%',#{trueName},'%')


		AND a.idcard = #{idcard}


		AND a.mobile = #{mobile}


		AND a.capacity = #{capacity}


		AND a.work_place LIKE 
			'%'||#{workPlace}||'%'
			'%'+#{workPlace}+'%'
			concat('%',#{workPlace},'%')


		AND a.position = #{position}


		AND a.region = #{region}


		AND a.user_abs = #{userAbs}


		AND a.company_name LIKE 
			'%'||#{companyName}||'%'
			'%'+#{companyName}+'%'
			concat('%',#{companyName},'%')


		AND a.email = #{email}




		ORDER BY ${page.orderBy}

SELECT 

FROM ps_user a






		ORDER BY ${page.orderBy}
