mapper.select:
SELECT 

FROM ps_user a

WHERE a.id = #{id}

SELECT
FROM ps_user a
AND a.id = #{id}
AND a.user_name LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
AND a.user_type = #{userType}
AND a.true_name LIKE '%'||#{trueName}||'%' '%'+#{trueName}+'%' concat('%',#{trueName},'%')
AND a.idcard LIKE '%'||#{idcard}||'%' '%'+#{idcard}+'%' concat('%',#{idcard},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
AND a.email LIKE '%'||#{email}||'%' '%'+#{email}+'%' concat('%',#{email},'%')
AND a.company_name LIKE '%'||#{companyName}||'%' '%'+#{companyName}+'%' concat('%',#{companyName},'%')
AND a.region LIKE '%'||#{region}||'%' '%'+#{region}+'%' concat('%',#{region},'%')
AND a.capacity = #{capacity}
AND a.work_place LIKE '%'||#{workPlace}||'%' '%'+#{workPlace}+'%' concat('%',#{workPlace},'%')
AND a.position LIKE '%'||#{position}||'%' '%'+#{position}+'%' concat('%',#{position},'%')
AND a.portrait LIKE '%'||#{portrait}||'%' '%'+#{portrait}+'%' concat('%',#{portrait},'%')
AND a.user_abs LIKE '%'||#{userAbs}||'%' '%'+#{userAbs}+'%' concat('%',#{userAbs},'%')
AND a.is_employee = #{isEmployee}
AND a.display_name LIKE '%'||#{displayName}||'%' '%'+#{displayName}+'%' concat('%',#{displayName},'%')
AND a.user_source_id = #{userSourceId}
AND a.status_code = #{statusCode}
AND a.create_time >= #{createTime}
AND a.last_login_time >= #{lastLoginTime}
ORDER BY ${page.orderBy}

SELECT 

FROM ps_user a






		ORDER BY ${page.orderBy}
